# MININAL EXAMPLE OF HOW TO INTEGRATE WITH TITAN TWO GPC SCRIPT
# ==============================================================================
'''-----------------------------------------------------------------------------
fix32 stick_1_x, stick_1_y, stick_1_dz;
bool rapidfire;

main {
    if(gcv_ready()) {
        switch(gcv_read(0)) {
            case 0: {
                rapidfire = gcv_read(1);
            } break;
            case 1: {
                gcv_read(1, &stick_1_x);
                gcv_read(5, &stick_1_y);
                gcv_read(9, &stick_1_dz);
            } break;
            case 2: {
                int16 hoffset, voffset;
                gcv_read(1, &hoffset);
                gcv_read(3, &voffset);
                mxyconverter_hoffset(hoffset);
                mxyconverter_voffset(voffset);
            } break;
            case 3: {
                gcv_config_check();
            } break;
        }
    }
    
    if(get_actual(BUTTON_5)) {
        if(stick_1_x || stick_1_y) {
            set_val(STICK_1_X, clamp(get_val(STICK_1_X) + stick_1_x, -100.0, 100.0));
            set_val(STICK_1_Y, clamp(get_val(STICK_1_Y) + stick_1_y, -100.0, 100.0));
        }
        if(rapidfire) {
            combo_run(Rapidfire);
        }
    } else if(event_release(BUTTON_5)) {
        stick_1_x = stick_1_y = 0.0;
        mxyconverter_hoffset(0);
        mxyconverter_voffset(0);
        combo_stop(Rapidfire);
    }
}

combo Rapidfire {
    set_val(BUTTON_5, 100.0);
    wait(24);
    set_val(BUTTON_5, 0.0);
    wait(16);
}

void gcv_config_check() {
    uint8 data_set;
    fix32 global_hscale;
    fix32 global_vscale;
        
    data_set = gcv_read(1);
    gcv_read(2, &global_hscale);
    gcv_read(6, &global_vscale);
    
    printf("GCV Config: [DATA_SET: %d, GLOBAL_HSCALE: %.2f, GLOBAL_VSCALE: %.2f]", data_set, global_hscale, global_vscale);
    
    return;
}
-----------------------------------------------------------------------------'''

